      <div class="flex">
        <LEDDigit v-for="(digit, index) in digits" :key="index" :digit="digit" :glow-color="glowColor" />
      </div>
      <div class="text-yellow-400 text-xs md:text-sm mt-1 tracking-wider drop-shadow-[0_0_4px_rgba(255,255,0,0.5)]"
           :class="{
                'text-yellow-400 drop-shadow-[0_0_4px_rgba(255,255,0,0.5)]': glowColor === 'yellow',
                'text-red-500 drop-shadow-[0_0_4px_rgba(239,68,68,0.5)]': glowColor === 'red',
                'text-green-500 drop-shadow-[0_0_4px_rgba(34,197,94,0.5)]': glowColor === 'green',
