// --- Main Countdown Timer Component ---
export default {
  name: 'LEDCountdownTimer',
  components: {
    LEDDigit,
    ColonSeparator,
    DigitGroup
  },
  props: {
    config: {
      type: Object,
      required: true,
      validator(value) {
        // Basic validation
        return typeof value.totalDuration === 'number' && value.totalDuration >= 0 && value.labels && typeof value.labels === 'object';
        // Note: label and individual label keys are optional based on template logic
      }
    }
  },
  setup(props) {
    const timeLeft = ref(props.config.totalDuration);
    let intervalId = null;
    const minutes = computed(() => Math.floor(timeLeft.value / 60000));
    const seconds = computed(() => Math.floor(timeLeft.value % 60000 / 1000));
    // Display milliseconds as two digits (00-99)
    const milliseconds = computed(() => Math.floor(timeLeft.value % 1000 / 10));
    const minutesDigits = computed(() => [Math.floor(minutes.value / 10), minutes.value % 10]);
    const secondsDigits = computed(() => [Math.floor(seconds.value / 10), seconds.value % 10]);
    const millisecondsDigits = computed(() => [Math.floor(milliseconds.value / 10), milliseconds.value % 10]);
    const startCountdown = () => {
      // Clear any existing interval to prevent multiple timers
      if (intervalId) clearInterval(intervalId);
      intervalId = setInterval(() => {
        if (timeLeft.value <= 0) {
          clearInterval(intervalId);
          timeLeft.value = 0; // Ensure it stops at 00:00:00
        } else {
          timeLeft.value -= 10; // Update every 10ms for smoother millisecond display
        }
      }, 10);
    };
    const resetCountdown = () => {
      if (intervalId) clearInterval(intervalId);
      timeLeft.value = props.config.totalDuration;
      startCountdown(); // Restart after reset
    };
    onMounted(() => {
      startCountdown();
    });
    onBeforeUnmount(() => {
      if (intervalId) clearInterval(intervalId);
    });

    // Expose reset function if needed by parent
    // provide('resetCountdown', resetCountdown);

    return {
      timeLeft,
      minutes,
      seconds,
      milliseconds,
      minutesDigits,
      secondsDigits,
      millisecondsDigits,
      resetCountdown // Expose reset function
    };
  }
};
