import { ref, onMounted, onUnmounted } from 'vue';
import Background from './components/background/BackgroundDefault.vue';
import Content from './components/content/ContentDefault.vue';
import ExitConfirm from './components/exitconfirm/ExitConfirmDefault.vue';
import Redirect from './components/redirect/RedirectDefault.vue';
import config from '/landing.config.js';
import LEDCountdown from './components/countdown/LEDCountdown.vue';

// 重定向组件引用

export default {
  __name: 'App',
  setup(__props, {
    expose: __expose
  }) {
    __expose();
    const redirectComponent = ref(null);

    // 退出确认弹窗状态
    const showExitConfirmModal = ref(false);

    // 处理CTA按钮点击
    const handleCtaClick = () => {
      // 点击按钮时立即重定向
      if (redirectComponent.value) {
        redirectComponent.value.skipCountdown();
      }
    };

    // 处理用户选择留下
    const handleStay = () => {
      showExitConfirmModal.value = false;
      console.log('User chose to stay');
      // 可以添加留下后的逻辑，如显示额外优惠等
    };

    // 处理用户选择离开
    const handleLeave = () => {
      showExitConfirmModal.value = false;
      console.log('User chose to leave');
      // 用户明确选择离开后允许页面跳转
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };

    // 处理页面离开前事件
    const handleBeforeUnload = e => {
      e.preventDefault();
      showExitConfirmModal.value = true;
      // Chrome需要返回一个值
      e.returnValue = '';
    };
    onMounted(() => {
      // 监听页面离开前事件
      window.addEventListener('beforeunload', handleBeforeUnload);
    });
    onUnmounted(() => {
      // 清理事件监听
      window.removeEventListener('beforeunload', handleBeforeUnload);
    });
    const __returned__ = {
      redirectComponent,
      showExitConfirmModal,
      handleCtaClick,
      handleStay,
      handleLeave,
      handleBeforeUnload,
      ref,
      onMounted,
      onUnmounted,
      Background,
      Content,
      ExitConfirm,
      Redirect,
      get config() {
        return config;
      },
      LEDCountdown
    };
    Object.defineProperty(__returned__, '__isScriptSetup', {
      enumerable: false,
      value: true
    });
    return __returned__;
  }
};
